import Axios from "axios";
import { useEffect, useState } from "react";
import Nav from "../Home/Nav";


export function Ethscan() {

  const [records, setrecords] = useState([]);

  useEffect(() => {
    Axios.get(
      `https://api-rinkeby.etherscan.io/api?module=account&action=txlist&address=0x25Bc35afD7b583CAf62F30a7AD9882f308D0c531&sort=desc`
    ).then((res) => {
      setrecords(res.data.result);
      console.log(res.data.result)
    });
  }, []);

  return (
    <div className="App crypto">
      <Nav />
      <h1>Get Transactions</h1>


      <div className="crypto__coins">

        <div >
          <table>
            <tr>
              <th>Block HashID</th>
              <th>Block Number</th>
              <th>HashID</th>
              <th>From</th>
              <th>To</th>



            </tr>
            {records.map((val, id) => {
                return (
                  <>
                    <tr id={id}>
                      <td className="rank">{val.hash}</td>
                      <td className="symbol">{val.blockNumber}</td>

                      <td>â‚¹{val.price}</td>
                      <td>  {val.priceChange1h > 0 ? (
                        <span className="green">{val.priceChange1h}</span>
                      ) : (
                        <span className="red">{val.priceChange1h}</span>
                      )}</td>
                      <td>  {val.priceChange1d > 0 ? (
                        <span className="green">{val.priceChange1d}</span>
                      ) : (
                        <span className="red">{val.priceChange1d}</span>
                      )}</td>
                      <td>  {val.priceChange1d > 0 ? (
                        <span className="green">{val.priceChange1w}</span>
                      ) : (
                        <span className="red">{val.priceChange1w}</span>
                      )}</td>

                    </tr>
                  </>
                );
              })}


          </table>
        </div>

      </div>
    </div>
  )
}
