import './style.css';


import React, { useContext } from "react";
import { TransactionContext } from '../../context/TransactionContext'
import Nav from '../Home/Nav';

export function Home() {

  const { connectWallet, connectAccount, formdata, sendformData, handleChange } = useContext(TransactionContext);
  return (
    <div className="App">

      <Nav />
      {!connectWallet &&
        <button
          type="button"
          onClick={connectWallet}
          className="flex flex-row justify-center items-center my-5 bg-[#2952e3] p-3 rounded-full cursor-pointer hover:bg-[#2546bd]"
        >Connect wallet</button>}

      <h3>Logged Account : {connectAccount}</h3>

      <div className="col mt-4">
        <h2>Transaction Detail</h2>
        <div class="form-group">
          <h5 for="" class="form-label mt-4">Account Address</h5>
          <input type="text" class="form-control" aria-describedby="emailHelp" placeholder="Account Address" name="addressTo" handleChange={handleChange} />

          <h5 for="" class="form-label mt-4">Amount</h5>
          <input type="text" class="form-control" aria-describedby="emailHelp" placeholder="Amount" name="amount"  handleChange={handleChange} />

          <h5 for="" class="form-label mt-4">Add GIF</h5>
          <input type="text" class="form-control" aria-describedby="emailHelp" placeholder="Add GIF" name="keyword"  handleChange={handleChange} />

          <h5 for="" class="form-label mt-4">Message </h5>
          <input type="text" class="form-control" aria-describedby="emailHelp" placeholder="Message" name="message"  handleChange={handleChange} />

          <br />
          <br />
          <button type="button" class="btn btn-primary mt-4" >Send Now</button>
        </div>
      </div>

    </div >
  );
}
