import Axios from "axios";
import { useEffect, useState } from "react";
import Nav from "../Home/Nav";

export function Ethscan() {

  const [records, setrecords] = useState([]);

  useEffect(() => {
    Axios.get(
      `https://api-rinkeby.etherscan.io/api?module=account&action=txlist&address=0x25Bc35afD7b583CAf62F30a7AD9882f308D0c531&sort=desc`
    ).then((res) => {
      setrecords(res.data.result);
      console.log(res.data.result)
    });
  }, []);

  return (
    <div className="App crypto">
      <Nav />
      <h1>Get Transactions</h1>


      <div className="crypto__coins">

        <div >
          <table>
            <tr>
              <th>HashID</th>
              <th>Block Number</th>
              <th>From</th>
              <th>To</th>
              <th>Ethereum</th>



            </tr>
            {records.map((val, id) => {
              return (
                <>
                  <tr id={id}>
                    <td className="rank">{val.hash.slice(0,10)}..</td>
                    <td className="symbol">{val.blockNumber}</td>

                    <td>{val.from.slice(0,10)}..</td>
                    <td>{val.to.slice(0,10)}..</td>
                    <td>{ val.value / 10**18}</td>




                  </tr>
                </>
              );
            })}


          </table>
        </div>

      </div>
    </div>
  )
}
